sys_script_include.script:
var StockQuote = Class.create();
StockQuote.prototype = {
    initialize: function(stock) {
		this.stock = stock;
    },
	process : function() {
		try {
			var body = {"ticker": this.stock};
			var restMessage = new sn_ws.RESTMessageV2('x_20227_custom_top.Google Cloud Function', 'POST');
			restMessage.setRequestBody(JSON.stringify(body));
			var response = restMessage.execute();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();
			return parseFloat(responseBody).toFixed(2);
		} catch(ex) {
			gs.error("{0} for StockQuote proccess()", ex.message);
		}
	},
    type: 'StockQuote'
};
